using UnityEngine;using System.Collections;public class EnemyManager : MonoBehaviour{    // enemyの初期総数。    const int CONST_ENEMY_DEFAULT_NUM = 20;    // enemyを生成するべき境界値の設定    const int CONST_ENEMY_LOWER_NUM = 15;    // 境界値になったら生成すべきenemyの個体数設定    const int CONST_ENEMY_POP_NUM = 20;    // enemy生成時のZ軸方向の範囲    // Unityのワールド座標Z+方向の範囲    const float FRONT_Z = 50.0f;    // Unityのワールド座標Z-方向の範囲    const float BACK_Z = 10.0f;    // エネミー総数    int enemyTotalNumber = CONST_ENEMY_DEFAULT_NUM;    // 現在のenemyの総数を取得する関数    int EnemyTotalNumber()    {        return enemyTotalNumber;    }    // enemyTotalNumberの数値減少　(敵が死ぬ時に呼ぶ    public void enemyDecrease()    {        if (enemyTotalNumber > 0) { enemyTotalNumber--; }    }    // enemyTotalNumberの数値増加　(一定値以下になって生成するときに呼ぶ    public void enemyIncrease()    {        enemyTotalNumber += CONST_ENEMY_POP_NUM;    }    // enemyを動的生成用のPrefab    public GameObject enemyPrefab;    // enemyの親になるオブジェクト    public GameObject EnemyParent;    // 壁オブジェクト（enemyを生成する範囲の基となる）    public GameObject Wall;    // 指定オブジェクトの中心座標取得とサイズ取得    Vector3 GetPosByObj(GameObject Obj)    {        return Obj.transform.position;    }    // 指定オブジェクトのサイズ取得    Vector3 GetSizeByObj(GameObject Obj)    {        return Obj.transform.localScale;    }    // enemyのPosition決定処理    Vector3 GetInstantiatePosition(GameObject obj)    {        //指定オブジェクトの中心座標とサイズ取得        Vector3 objPos = GetPosByObj(obj);        Vector3 objSize = GetSizeByObj(obj);        //enemyをランダムに生成する範囲の計算。(zのenemy出現範囲は定数で設定しているため計算なし)        float leftPosX = objPos.x - (objSize.x / 2);        float rightPosX = objPos.x + (objSize.x / 2);        float topPosY = objPos.y + (objSize.y / 2);        float downPosY = objPos.y - (objSize.y / 2);        //各座標をランダムに取得        float x = Random.Range(leftPosX, rightPosX);        float y = Random.Range(topPosY, downPosY);        float z = Random.Range(BACK_Z, FRONT_Z);        //生成位置を返す。        return new Vector3(x, y, z);    }    // Use this for initialization    void Start()    {        // 複数体を指定の範囲位置にランダム生成(範囲は防御壁の範囲）        for (int i = 0; i < CONST_ENEMY_DEFAULT_NUM; i++)        {            //生成する位置のvector3 positionをランダムになるように設定する。            Vector3 enemyInstansPos = GetInstantiatePosition(Wall);            GameObject enemy = (GameObject)Instantiate(enemyPrefab, enemyInstansPos, Quaternion.identity);            //生成したenemyオブジェクトの親を設定            enemy.transform.parent = EnemyParent.transform;        }    }    // Update is called once per frame    void Update()    {        Debug.Log(enemyTotalNumber);        // 敵の総数が一定未満になったら一定量生成する        if (enemyTotalNumber < CONST_ENEMY_LOWER_NUM)        {            for (int i = 0; i < CONST_ENEMY_POP_NUM; i++)            {                //生成する位置のvector3 positionをランダムになるように設定する。                Vector3 enemyInstansPos = GetInstantiatePosition(Wall);                GameObject enemy = (GameObject)Instantiate(enemyPrefab, enemyInstansPos, Quaternion.identity);                //生成したenemyオブジェクトの親を設定                enemy.transform.parent = EnemyParent.transform;            }            enemyIncrease();        }    }}